import React from 'react';

const Modalnotifikasi = ({ isOpen, onClose, title, message, type = 'info' }) => {
  if (!isOpen) return null;

  let headerBgClass = 'bg-gray-500';
  let titleColorClass = 'text-white';
  let messageColorClass = 'text-gray-800';
  let buttonBgClass = 'bg-blue-600 hover:bg-blue-700';

  switch (type) {
    case 'success':
      headerBgClass = 'bg-green-500';
      buttonBgClass = 'bg-green-600 hover:bg-green-700';
      break;
    case 'error':
      headerBgClass = 'bg-red-500';
      buttonBgClass = 'bg-red-600 hover:bg-red-700';
      messageColorClass = 'text-red-700'; // Make error messages stand out
      break;
    case 'warning':
      headerBgClass = 'bg-yellow-500';
      buttonBgClass = 'bg-yellow-600 hover:bg-yellow-700';
      messageColorClass = 'text-yellow-800';
      break;
    default: // info
      // default classes are already set
      break;
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-sm overflow-hidden">
        {/* Modal Header */}
        <div className={`${headerBgClass} px-6 py-3 rounded-t-lg`}>
          <h3 className={`text-lg font-semibold ${titleColorClass}`}>{title}</h3>
        </div>

        {/* Modal Body */}
        <div className="p-6">
          <p className={`text-sm ${messageColorClass} mb-6`}>{message}</p>
          <div className="flex justify-end">
            <button
              onClick={onClose}
              className={`px-6 py-2 rounded-md font-semibold text-white ${buttonBgClass} transition-colors duration-200 shadow`}
            >
              OK
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modalnotifikasi;
