import React, { useState, useEffect } from 'react';

function ModalKategori({ isOpen, onClose, onSave, initialData, accounts, mode }) {
  const [name, setName] = useState('');
  const [totalAmount, setTotalAmount] = useState(''); // Total budget untuk kategori
  // State baru untuk menyimpan alokasi per akun: [{ account_id: '...', amount: '...' }]
  const [accountAllocations, setAccountAllocations] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (isOpen) {
      setError('');
      if (mode === 'edit' && initialData) {
        setName(initialData.name || '');
        setTotalAmount(''); // Tidak ada totalAmount untuk edit kategori saja
        setAccountAllocations([]); // Tidak ada alokasi untuk edit kategori saja
      } else {
        setName('');
        setTotalAmount('');
        setAccountAllocations([]);
      }
    }
  }, [isOpen, initialData, mode]);

  const handleAllocationChange = (accountId, amount) => {
    setAccountAllocations(prev => {
      const existingIndex = prev.findIndex(alloc => alloc.account_id === accountId);
      if (existingIndex > -1) {
        // Update existing allocation
        const updated = [...prev];
        updated[existingIndex] = { account_id: accountId, amount: amount };
        return updated;
      } else {
        // Add new allocation
        return [...prev, { account_id: accountId, amount: amount }];
      }
    });
  };

  const handleCheckboxChange = (accountId, isChecked) => {
    setAccountAllocations(prev => {
      if (isChecked) {
        // Tambahkan akun jika dicentang, dengan jumlah awal 0
        return [...prev, { account_id: accountId, amount: '' }];
      } else {
        // Hapus akun jika tidak dicentang
        return prev.filter(alloc => alloc.account_id !== accountId);
      }
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');

    if (!name.trim()) {
      setError('Nama kategori tidak boleh kosong.');
      return;
    }

    if (mode === 'add') {
      const parsedTotalAmount = parseFloat(totalAmount);
      if (isNaN(parsedTotalAmount) || parsedTotalAmount <= 0) {
        setError('Total nominal budget tidak valid.');
        return;
      }

      if (accountAllocations.length === 0) {
        setError('Harap pilih setidaknya satu akun untuk alokasi dana.');
        return;
      }

      let sumOfAllocations = 0;
      const validAllocations = [];
      for (const alloc of accountAllocations) {
        const parsedAmount = parseFloat(alloc.amount);
        if (isNaN(parsedAmount) || parsedAmount <= 0) {
          setError(`Nominal alokasi untuk akun ${accounts.find(a => a.id === alloc.account_id)?.name || alloc.account_id} tidak valid.`);
          return;
        }
        validAllocations.push({ account_id: alloc.account_id, amount: parsedAmount });
        sumOfAllocations += parsedAmount;
      }

      if (sumOfAllocations !== parsedTotalAmount) {
        setError(`Total alokasi per akun (Rp ${sumOfAllocations.toLocaleString('id-ID')}) tidak sama dengan Total Budget (Rp ${parsedTotalAmount.toLocaleString('id-ID')}).`);
        return;
      }

      onSave({ name, total_amount: parsedTotalAmount, allocations: validAllocations });

    } else if (mode === 'edit') {
      onSave({ id: initialData.id, name });
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4">
          {mode === 'add' ? 'Tambah Kategori Baru' : 'Edit Kategori'}
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="name" className="block text-gray-700 text-sm font-bold mb-2">
              Nama Kategori:
            </label>
            <input
              type="text"
              id="name"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          {mode === 'add' && (
            <>
              <div className="mb-4">
                <label htmlFor="totalAmount" className="block text-gray-700 text-sm font-bold mb-2">
                  Total Budget Kategori (Rp):
                </label>
                <input
                  type="number"
                  id="totalAmount"
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                  value={totalAmount}
                  onChange={(e) => setTotalAmount(e.target.value)}
                  min="0"
                  step="any"
                  required
                />
              </div>

              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Alokasi dari Akun:
                </label>
                {accounts.length > 0 ? (
                  accounts.map(account => (
                    <div key={account.id} className="flex items-center mb-2">
                      <input
                        type="checkbox"
                        id={`account-${account.id}`}
                        className="mr-2"
                        checked={accountAllocations.some(alloc => alloc.account_id === account.id)}
                        onChange={(e) => handleCheckboxChange(account.id, e.target.checked)}
                      />
                      <label htmlFor={`account-${account.id}`} className="text-gray-700 mr-2 flex-grow">
                        {account.name} (Saldo: Rp {Number(account.balance).toLocaleString('id-ID')})
                      </label>
                      {accountAllocations.some(alloc => alloc.account_id === account.id) && (
                        <input
                          type="number"
                          className="shadow appearance-none border rounded py-1 px-2 text-gray-700 leading-tight focus:outline-none focus:shadow-outline w-1/3"
                          value={accountAllocations.find(alloc => alloc.account_id === account.id)?.amount || ''}
                          onChange={(e) => handleAllocationChange(account.id, e.target.value)}
                          min="0"
                          step="any"
                          placeholder="Jumlah"
                          required
                        />
                      )}
                    </div>
                  ))
                ) : (
                  <p className="text-gray-500 text-sm">Tidak ada akun tersedia. Harap buat akun terlebih dahulu.</p>
                )}
              </div>
            </>
          )}

          {error && <p className="text-red-500 text-xs italic mb-4">{error}</p>}

          <div className="flex justify-end gap-2">
            <button
              type="button"
              onClick={onClose}
              className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline"
            >
              Batal
            </button>
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline"
            >
              {mode === 'add' ? 'Tambah' : 'Simpan'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ModalKategori;
