export const transformBudgetsData = (data) => {
  const transformedData = {};

  data.forEach(item => {
    // Format bulan menjadi 'MM' (dua digit dengan leading zero jika perlu)
    const monthFormatted = String(item.month).padStart(2, '0');
    const monthYearKey = `${item.year}-${monthFormatted}`;

    // Jika kunci bulan-tahun belum ada, inisialisasi dengan array kosong
    if (!transformedData[monthYearKey]) {
      transformedData[monthYearKey] = [];
    }

    // Tambahkan item budget ke array bulan yang sesuai
    // Pastikan untuk menghapus properti 'month' dan 'year' jika tidak lagi diperlukan di frontend
    const { month, year, ...rest } = item; // Destructure untuk menghapus month dan year
    transformedData[monthYearKey].push(rest);
  });

  return transformedData;
};

// --- Untuk data Akun juga jika strukturnya serupa: ---
export const transformAkunData = (data) => {
  const transformedData = {};

  data.forEach(item => {
    const monthFormatted = String(item.month).padStart(2, '0');
    const monthYearKey = `${item.year}-${monthFormatted}`;

    if (!transformedData[monthYearKey]) {
      transformedData[monthYearKey] = [];
    }
    const { month, year, ...rest } = item;
    transformedData[monthYearKey].push(rest);
  });

  return transformedData;
};
