import React, { useEffect, useState, useCallback } from "react";
import ModalAddMonth from "./ModalAddMonth";
import IconPickerModal from "./IconPickerModal";
import Recent from './RecentTransaction';
import FilterTransaksi from './FilterTransaksi';
import ChartKeuangan from './ChartKeuangan';
import ModalTransaksi from './ModalTransaksi';
import ModalKategori from './ModalKategori';
import ModalAkun from './ModalAkun';
import { transformBudgetsData } from './util/dataTransformer'; // Import fungsi transformasi
import { useNavigate } from 'react-router-dom';
import DropdownAksi from "./DropdownButton";
import { 
  getTransactions, getBudgets, getCategories, deleteTransaction, 
  deleteCategory, setBudget,createTransaction, updateTransaction, 
  createCategory, updateCategory, createAccount, getAccounts, 
  updateAccount, deleteAccount
} from './apiservice';

// Fungsi helper untuk memformat bulan (Sudah diperbaiki agar lebih robust)
const formatMonth = (monthString) => {
    if (!monthString) {
        console.warn("formatMonth received empty or null string.");
        return '';
    }
    const parts = monthString.split('-');
    
    if (parts.length !== 2) {
        console.error("formatMonth: Invalid month string format. Expected 'YYYY-MM', received:", monthString);
        return monthString;
    }

    const year = parseInt(parts[0], 10);
    const month = parseInt(parts[1], 10);

    if (isNaN(year) || isNaN(month)) {
        console.error("formatMonth: Non-numeric year or month. Received:", monthString, { year, month });
        return monthString;
    }

    const date = new Date(year, month - 1); // Bulan di JS adalah 0-indexed

    if (isNaN(date.getTime())) { // Memeriksa apakah objek Date valid
        console.error("formatMonth: Created invalid Date object for:", monthString, "Date object:", date);
        return monthString;
    }

    return new Intl.DateTimeFormat('id-ID', { year: 'numeric', month: 'long' }).format(date);
};

function App() {
  const navigate = useNavigate();

  // State Aplikasi
  const [isAccountModalOpen, setAccountModalOpen] = useState(false);
  const [accounts, setAccounts] = useState([]);
  const [budgetsByMonth, setBudgetsByMonth] = useState({}); // Akan berisi semua budget per bulan
  const [transactions, setTransactions] = useState([]); // Pengeluaran
  const [pendapatan, setPendapatan] = useState([]); // Pendapatan
  const [categories, setCategories] = useState([]);
  const [isTransactionModalOpen, setTransactionModalOpen] = useState(false);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [isCategoryModalOpen, setCategoryModalOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [editingBudgetId, setEditingBudgetId] = useState(null);
  const [editingBudgetAmount, setEditingBudgetAmount] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isModalAddMonthOpen, setIsModalAddMonthOpen] = useState(false);
  const [selectedTab, setSelectedTab] = useState("Terkini");
  const [selectedTabPendapatan, setSelectedTabPendapatan] = useState("Terkini");
  const [isIconPickerOpen, setIconPickerOpen] = useState(false);
  const [iconTargetCategory, setIconTargetCategory] = useState(null);
  
  // Fungsi untuk mendapatkan bulan dan tahun saat ini dalam format YYYY-MM
  const getCurrentMonthYearKey = () => {
    const d = new Date();
    return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}`;
  };
  const [selectedMonth, setSelectedMonth] = useState(getCurrentMonthYearKey());

  // Fungsi untuk mengambil semua data dari backend
  const fetchData = useCallback(async () => {
    setIsLoading(true);
    try {
      // PENTING: getBudgets() dipanggil TANPA parameter year, month
      // Asumsi: Backend akan mengembalikan SEMUA data budget untuk user yang login
      const [accountData, allBudgetsRawData, transactionData, categoryData] = await Promise.all([
        getAccounts(),
        getBudgets(), // <-- Ini yang diperbaiki!
        getTransactions(),
        getCategories()
      ]);

      setAccounts(accountData);
      setCategories(categoryData);
      
      // Transformasi data budget mentah ke format budgetsByMonth yang diinginkan
      const transformedBudgets = transformBudgetsData(allBudgetsRawData);
      setBudgetsByMonth(transformedBudgets); // <-- Ini akan berisi semua bulan
      
      // Filter transaksi (expense dan income)
      const expenseTxs = transactionData.filter(tx => tx.type === 'expense');
      const incomeTxs = transactionData.filter(tx => tx.type === 'income');
      setTransactions(expenseTxs);
      setPendapatan(incomeTxs);

      // Logika untuk mengatur selectedMonth awal
      const sortedMonths = Object.keys(transformedBudgets).sort((a, b) => new Date(a) - new Date(b));
      if (sortedMonths.length > 0) {
        // Jika ada bulan yang tersedia, coba pertahankan selectedMonth saat ini
        // atau set ke bulan terbaru jika selectedMonth tidak valid/kosong
        if (!selectedMonth || !transformedBudgets[selectedMonth]) {
          setSelectedMonth(sortedMonths[sortedMonths.length - 1]); // Set ke bulan terbaru
        }
      } else {
        // Jika tidak ada bulan sama sekali di database, set ke bulan saat ini
        setSelectedMonth(getCurrentMonthYearKey());
      }

    } catch (error) {
      console.error("Gagal mengambil data:", error);
      // Di sini Anda bisa menambahkan logika redirect ke halaman login jika token tidak valid
      // navigate('/'); 
    } finally {
      setIsLoading(false);
    }
  }, [selectedMonth]); // selectedMonth sebagai dependency karena digunakan dalam logika penentuan bulan awal

  // Panggil fetchData saat komponen mount
  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // Handler untuk menambah bulan baru dari modal
  const handleAddMonth = async (newMonthYYYYMM) => {
    if (budgetsByMonth[newMonthYYYYMM]) {
      alert("Maaf, bulan ini sudah ada!");
      return;
    }

    setIsLoading(true);
    try {
      const [year, month] = newMonthYYYYMM.split('-');
      
      if (categories.length === 0) {
        alert("Tidak ada kategori yang ditemukan. Silakan tambahkan kategori terlebih dahulu.");
        setIsLoading(false);
        return;
      }

      // Buat array Promise untuk setiap kategori untuk menginisialisasi budget 0 di backend
      const budgetPromises = categories.map(async (category) => {
        await setBudget({
          category_id: category.id,
          amount: 0, // Inisialisasi dengan budget nol
          year: parseInt(year),
          month: parseInt(month),
        });
      });

      await Promise.all(budgetPromises); // Tunggu semua inisialisasi selesai
      await fetchData(); // Refresh data setelah menambahkan bulan baru
      setSelectedMonth(newMonthYYYYMM);
      setIsModalAddMonthOpen(false);
      alert(`Bulan ${formatMonth(newMonthYYYYMM)} berhasil ditambahkan.`);
    } catch (error) {
      console.error("Gagal menambahkan bulan:", error);
      alert("Terjadi kesalahan saat menambahkan bulan: " + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Handler untuk menyimpan perubahan budget
  const handleSaveBudget = async (categoryId, amount) => {
    if (!selectedMonth) {
      alert("Pilih bulan terlebih dahulu!");
      return;
    }
    setIsLoading(true);
    try {
      const [year, month] = selectedMonth.split('-');
      const parsedAmount = parseFloat(amount) || 0;
      await setBudget({
        category_id: categoryId,
        amount: parsedAmount,
        year: parseInt(year),
        month: parseInt(month),
      });

      // Update state lokal budgetsByMonth secara langsung (lebih cepat dari fetchData penuh)
      setBudgetsByMonth(prevBudgets => {
        const currentMonthBudgets = prevBudgets[selectedMonth] || [];
        const updatedBudgets = currentMonthBudgets.map(b =>
          b.category_id === categoryId ? { ...b, amount: parsedAmount } : b
        );
        return {
          ...prevBudgets,
          [selectedMonth]: updatedBudgets
        };
      });

      alert('Budget berhasil disimpan.');
      // fetchData(); // Tidak perlu fetchData jika update state lokal sudah cukup untuk ini
    } catch (error) {
      console.error("Gagal simpan budget:", error);
      alert(error.message);
    } finally {
      setEditingBudgetId(null);
      setIsLoading(false);
    }
  };

  // Handler untuk menghapus transaksi
  const handleDeleteTransaction = async (transactionId) => {
    if (window.confirm("Anda yakin ingin menghapus transaksi ini?")) {
      try {
        await deleteTransaction(transactionId);
        // Update state transaksi secara manual
        setTransactions(prev => prev.filter(tx => tx.id !== transactionId));
        setPendapatan(prev => prev.filter(p => p.id !== transactionId));
        // Panggil fetchData karena ini mempengaruhi saldo akun dan 'used' budget
        fetchData(); 
        alert('Transaksi berhasil dihapus.');
      } catch (error) {
        console.error("Gagal menghapus transaksi:", error);
        alert(error.message);
      }
    }
  };
  
  // Handler untuk menghapus kategori
  const handleHapusKategori = async (categoryId) => {
    if (window.confirm("Yakin ingin menghapus kategori ini?")) {
      try {
        await deleteCategory(categoryId);
        // Update state categories secara langsung
        setCategories(prev => prev.filter(cat => cat.id !== categoryId));
        
        // Hapus juga dari budgetsByMonth
        setBudgetsByMonth(prev => {
          const newBudgets = { ...prev };
          for (const monthKey in newBudgets) {
            if (newBudgets[monthKey]) {
                newBudgets[monthKey] = newBudgets[monthKey].filter(b => b.category_id !== categoryId);
            }
          }
          return newBudgets;
        });
        
        // Panggil fetchData karena penghapusan kategori bisa memengaruhi transaksi dan chart
        fetchData(); 
        alert('Kategori berhasil dihapus.');
      } catch (error) {
        console.error("Gagal hapus kategori:", error);
        alert(error.message);
      }
    }
  };

  // Handler untuk membuka modal transaksi dalam mode edit
  const handleOpenEditTransactionModal = (transaction) => {
    setSelectedTransaction(transaction);
    setTransactionModalOpen(true);
  };

  // Handler untuk menyimpan transaksi (tambah/edit)
  const handleSaveTransaction = async (data) => {
    setIsLoading(true);
    try {
      let savedTransaction;
      if (data.id) {
        const { type, ...updateData } = data;
        await updateTransaction(data.id, updateData);
        savedTransaction = { ...data };
      } else {
        savedTransaction = await createTransaction(data);
      }

      // Update state transaksi secara langsung
      if (savedTransaction.type === 'expense') {
        setTransactions(prev => {
          if (data.id) {
            return prev.map(tx => tx.id === savedTransaction.id ? savedTransaction : tx);
          } else {
            return [...prev, savedTransaction];
          }
        });
      } else if (savedTransaction.type === 'income') {
        setPendapatan(prev => {
          if (data.id) {
            return prev.map(tx => tx.id === savedTransaction.id ? savedTransaction : tx);
          } else {
            return [...prev, savedTransaction];
          }
        });
      }

      setTransactionModalOpen(false);
      // Panggil fetchData untuk memastikan semua data (terutama saldo akun dan budget 'used') terupdate
      fetchData(); 
      alert('Transaksi berhasil disimpan!');
    } catch (error) {
      console.error('Gagal menyimpan transaksi:', error);
      alert(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Handler untuk membuka modal tambah kategori
  const handleOpenAddCategoryModal = () => {
    setSelectedCategory(null);
    setCategoryModalOpen(true);
  };

  // Handler untuk membuka modal edit kategori
  const handleOpenEditCategoryModal = (category) => {
    setSelectedCategory(category);
    setCategoryModalOpen(true);
  };

  // Handler untuk menyimpan kategori (tambah/edit)
  const handleSaveCategory = async (data) => {
    setIsLoading(true);
    try {
      if (data.id) {
        await updateCategory(data.id, { name: data.name });
        // Update state categories
        setCategories(prev => 
          prev.map(cat => cat.id === data.id ? { ...cat, name: data.name } : cat)
        );
        // Update category name in budgetsByMonth
        setBudgetsByMonth(prevBudgets => {
            const newBudgets = { ...prevBudgets };
            for (const monthKey in newBudgets) {
                if (newBudgets[monthKey]) {
                    newBudgets[monthKey] = newBudgets[monthKey].map(b =>
                        b.category_id === data.id ? { ...b, category_name: data.name } : b
                    );
                }
            }
            return newBudgets;
        });
        alert('Nama kategori berhasil diupdate!');
      } else {
        const newCategory = await createCategory({ name: data.name });
        setCategories(prev => [...prev, newCategory]);

        if (data.amount > 0) {
          const [year, month] = selectedMonth.split('-');
          const newBudget = await setBudget({ 
            category_id: newCategory.id,
            amount: data.amount,
            year: parseInt(year),
            month: parseInt(month),
          });
          // Update state budgetsByMonth
          setBudgetsByMonth(prevBudgets => {
            const currentMonthBudgets = prevBudgets[selectedMonth] || [];
            return {
              ...prevBudgets,
              [selectedMonth]: [...currentMonthBudgets, { ...newBudget, category_name: newCategory.name }]
            };
          });
        }
        alert('Kategori baru berhasil ditambahkan!');
      }
      setCategoryModalOpen(false);
    } catch (error) {
      console.error('Gagal menyimpan kategori:', error);
      alert(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Handler untuk membuka modal tambah akun
  const handleOpenAddAccountModal = () => setAccountModalOpen(true);

  // Handler untuk menyimpan akun (tambah/edit)
  const handleSaveAccount = async (data) => {
    setIsLoading(true);
    try {
      let savedAccount;
      if (data.id) {
        await updateAccount(data.id, data);
        savedAccount = { ...data };
      } else {
        savedAccount = await createAccount(data);
      }
      
      setAccounts(prev => {
        if (data.id) {
          return prev.map(acc => acc.id === savedAccount.id ? savedAccount : acc);
        } else {
          return [...prev, savedAccount];
        }
      });
      setAccountModalOpen(false);
      fetchData(); // Panggil fetchData karena perubahan akun bisa memengaruhi transaksi
      alert('Akun berhasil disimpan!');
    } catch (error) {
      alert(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Handler untuk menghapus akun
  const handleDeleteAccount = async (accountId) => {
    if(window.confirm('Yakin ingin menghapus akun ini? Aksi ini tidak bisa dibatalkan.')){
      try {
        await deleteAccount(accountId);
        setAccounts(prev => prev.filter(acc => acc.id !== accountId));
        fetchData(); // Panggil fetchData karena penghapusan akun mungkin memengaruhi transaksi
        alert('Akun berhasil dihapus.');
      } catch (error) {
        alert(error.message);
      }
    }
  }

  // Handler untuk membuka modal edit akun
  const handleOpenEditAccountModal = (account) => {
    setSelectedAccount(account);
    setAccountModalOpen(true);
  };

  // Handler logout
  const handleLogout = () => {
    localStorage.removeItem('userToken');
    navigate('/');
  };

  // Komponen Circular Progress
  const CircularProgress = ({ percentage }) => {
    const pct = Number(percentage);
    const radius = 20;
    const stroke = 4;
    const normalized = radius - stroke / 2;
    const circumference = 2 * Math.PI * normalized;
  
    const clamped = Math.min(pct, 100);
    const offset = circumference * (1 - clamped / 100);
  
    const color =
      pct > 99 ? 'stroke-red-500'
      : pct > 50 ? 'stroke-yellow-400'
      : pct > 0 ? 'stroke-green-400'
      : 'stroke-transparent';
  
    return (
      <svg className="w-5 h-5 md:w-6 md:h-6" viewBox="0 0 48 48">
        <circle
          className="stroke-gray-200"
          strokeWidth={stroke}
          fill="transparent"
          r={normalized}
          cx="24"
          cy="24"
        />
        <circle
          className={`transition-all duration-300 ${color}`}
          strokeWidth={stroke}
          fill="transparent"
          r={normalized}
          cx="24"
          cy="24"
          strokeDasharray={circumference}
          strokeDashoffset={offset}
          strokeLinecap="round"
        />
      </svg>
    );
  };

  // Handler untuk membuka modal pemilih ikon
  const handleOpenIconPicker = (category) => {
    setIconTargetCategory(category);
    setIconPickerOpen(true);
  };

  // Handler yang dipanggil setelah ikon dipilih dari modal
  const handleIconSelect = async (iconPath) => {
    if (!iconTargetCategory) return;
    setIsLoading(true);
    try {
      await updateCategory(iconTargetCategory.id, { icon_path: iconPath });
      // Update state categories secara langsung
      setCategories(prev => 
        prev.map(cat => 
          cat.id === iconTargetCategory.id ? { ...cat, icon_path: iconPath } : cat
        )
      );
      // Update icon_path in budgetsByMonth
        setBudgetsByMonth(prevBudgets => {
            const newBudgets = { ...prevBudgets };
            for (const monthKey in newBudgets) {
                if (newBudgets[monthKey]) {
                    newBudgets[monthKey] = newBudgets[monthKey].map(b =>
                        b.category_id === iconTargetCategory.id ? { ...b, icon_path: iconPath } : b
                    );
                }
            }
            return newBudgets;
        });

      setIconPickerOpen(false);
      alert('Ikon berhasil diupdate!');
    } catch (error) {
      console.error("Gagal update ikon:", error);
      alert(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Tampilkan loading screen jika isLoading true
  if (isLoading) {
    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-200">
            <div className="text-xl font-semibold text-gray-700">Memuat data...</div>
        </div>
    );
  }

  // Tampilan Utama Aplikasi
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-200">
      {/* Bagian Header */}
      <div className="flex flex-row w-full bg-black shadow-md">
        <h1 className="px-5 py-4 text-xl font-bold text-white">=</h1>
      </div>
  
      <div className="w-full bg-gray-700 shadow-md text-center py-9 md:py-32 lg:py-32 animate-fade-in">
        <h1 className="font-semibold text-white mb-4 text-4xl md:text-6xl lg:text-7xl">
          DuitKu
        </h1>
        <button
          onClick={handleLogout}
          className="absolute top-4 right-4 bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg shadow transition-colors duration-200"
        >
          Logout
        </button>
      </div>
      <div className="flex justify-center mt-5 animate-slide-in-up px-4 sm:px-6 md:px-8 lg:px-10">
        <div className="inline-flex rounded-full overflow-hidden shadow-md border border-gray-500">
          <button
            className="bg-gray-500 text-white px-6 py-2 text-sm md:text-base font-medium rounded-l-full"
          >
            Transaksi
          </button>
          <button
            onClick={() => navigate("/hutang")}
            className="bg-white text-gray-500 hover:bg-gray-100 px-6 py-2 text-sm md:text-base font-medium rounded-r-full transition-colors duration-200"
          >
            Hutang 
          </button>
        </div>
      </div>

      {/* Bagian Konten Utama (Kolom-kolom) - Responsif */}
      <div className="py-6 px-4 flex flex-col lg:flex-row bg-gray-200 w-full lg:px-10 animate-fade-in">
        
        {/* Kolom Kiri - Kategori & Daftar Akun */}
        <div className="flex flex-col w-full lg:w-1/6 lg:pr-4 mb-8 lg:mb-0">
          <div className="flex flex-col">
            <h1 className="bg-gray-500 rounded-md text-sm md:text-base lg:text-lg font-bold text-white px-2 py-1 mb-2 w-full sm:w-3/4 animate-fade-in-down">
              Kategori Bulanan
            </h1>
            <div className="mb-4 py-2 animate-slide-in-up" style={{ animationDelay: '0.2s' }}>
              <label htmlFor="month-select" className="text-sm font-semibold mr-2 text-gray-700">
                Pilih Bulan:
              </label>
              <select
                id="month-select"
                name="selectedMonthDropdown" // Tambahkan atribut name
                value={selectedMonth}
                onChange={(e) => {
                  const val = e.target.value;
                  if (val === "add_new") {
                    setIsModalAddMonthOpen(true);
                  } else {
                    setSelectedMonth(val);
                  }
                }}
                className="rounded-md border-gray-300 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50 transition-all duration-200 text-sm md:text-base"
              >
                {/* Render opsi bulan dari budgetsByMonth */}
                {Object.keys(budgetsByMonth)
                  .sort((a, b) => new Date(a) - new Date(b))
                  .map((monthKey) => (
                    <option key={monthKey} value={monthKey}>
                      {formatMonth(monthKey)}
                    </option>
                  ))
                }
                <option value="add_new">+ Tambah Bulan</option>
              </select>
            </div>
            <div className="flex flex-wrap gap-2 py-1 w-full justify-center sm:justify-start">
              {/* Tampilkan budget per kategori untuk bulan yang terpilih */}
              {selectedMonth && categories.length > 0 ? (
                categories.map((category, index) => {
                  // Cari budget data untuk kategori ini di bulan yang terpilih
                  const budgetData = budgetsByMonth[selectedMonth]?.find(b => b.category_id === category.id);
                  
                  // Pastikan amount dan used diinisialisasi ke 0 jika budgetData tidak ada
                  const budgetAmount = budgetData ? parseFloat(budgetData.amount) || 0 : 0;
                  const usedAmount = budgetData ? parseFloat(budgetData.used) || 0 : 0; 
                  
                  const percentage = budgetAmount > 0 ? (usedAmount / budgetAmount) * 100 : 0;
                  const isEditing = editingBudgetId === category.id;
  
                  return (
                    <div
                      key={category.id}
                      className="w-full sm:w-[calc(50%-0.5rem)] lg:w-full animate-slide-in-up"
                      style={{ animationDelay: `${0.2 + (0.1 * index)}s` }}
                    >
                      <div className="bg-white border rounded-xl px-2 py-1 gap-1 flex flex-col w-full border-gray-300 hover:shadow-md transition-shadow duration-200">
                        <div className="flex flex-row items-center">
                          <div
                              className="w-8 h-8 rounded-full border bg-gray-200 flex items-center justify-center cursor-pointer mr-2 flex-shrink-0 hover:scale-110 transition-transform duration-200"
                              onClick={() => handleOpenIconPicker(category)}
                              title="Ubah Ikon"
                            >
                              {category.icon_path ? (
                                    <img src={category.icon_path} alt={category.name} className="w-full h-full object-cover rounded-full" />
                                ) : (
                                    <span className="text-gray-500 text-xl font-light">+</span>
                                )}
                            </div>
                            <span className="text-xs md:text-sm lg:text-[12px] xl:text-[14px] text-left flex-grow">
                              {category.name}
                            </span>
                          <DropdownAksi
                              onEdit={() => handleOpenEditCategoryModal(category)}
                              onDelete={() => handleHapusKategori(category.id)}
                          />
                        </div>
                        <div className="text-xs md:text-sm lg:text-[12px] xl:text-[14px] mt-1 w-fit relative group inline-block">
                            {isEditing ? (
                                <input
                                    type="number"
                                    value={editingBudgetAmount}
                                    onChange={(e) => setEditingBudgetAmount(e.target.value)}
                                    onBlur={() => handleSaveBudget(category.id, editingBudgetAmount)}
                                    onKeyDown={(e) => { if (e.key === 'Enter') handleSaveBudget(category.id, editingBudgetAmount) }}
                                    autoFocus
                                    className="w-24 border-b-2 border-blue-500 outline-none"
                                />
                            ) : (
                                <span onClick={() => {
                                    setEditingBudgetId(category.id);
                                    setEditingBudgetAmount(budgetAmount);
                                }}
                                className="cursor-pointer"
                                >
                                    Rp {budgetAmount.toLocaleString('id-ID')}
                                </span>
                            )}
                        </div>
                        <div className="flex flex-row gap-1 items-center relative w-fit group cursor-pointer">
                          <div className="text-xs md:text-sm lg:text-[12px] xl:text-[14px]">
                            {percentage.toFixed(0)}%
                          </div>
                          <CircularProgress percentage={percentage} />
                          <div className="absolute left-full top-1/2 ml-2 -translate-y-1/2
                              bg-gray-500 text-white text-xs md:text-sm lg:text-[12px] xl:text-[14px] px-1 py-1 rounded 
                              opacity-0 group-hover:opacity-100 transition-all duration-200 
                              whitespace-nowrap z-10
                              pointer-events-none">
                            Pemakaian
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })
              ) : (
                <p className="p-2 text-sm text-gray-500 w-full text-center">
                    {selectedMonth ? "Tidak ada budget atau kategori untuk bulan ini." : "Pilih bulan untuk melihat budget."}
                    <br/>
                    {categories.length === 0 && "Tambahkan kategori terlebih dahulu."}
                </p>
              )}
  
              <button
                onClick={handleOpenAddCategoryModal}
                className="bg-gray-100 text-gray-300 text-xs md:text-sm xl:text-[15px] px-5 py-2 rounded hover:bg-gray-200 hover:text-gray-500 transition-colors duration-200 w-full sm:w-[calc(50%-0.5rem)] lg:w-full"
              >
                + Tambah Kategori
              </button>
            </div>
          </div>
          
          {/* Bagian Daftar Akun */}
          <div className="flex flex-col w-full mt-7">
              <h1 className="bg-gray-500 rounded-md text-sm md:text-base lg:text-lg font-bold text-white px-2 py-1 mb-2 w-full sm:w-3/4 animate-fade-in-down">
                Daftar Akun
              </h1>
              <div className="flex flex-wrap gap-2 py-1 w-full justify-center sm:justify-start">
                  {accounts.length > 0 ? (
                      accounts.map(account => (
                          <div key={account.id} className="w-full sm:w-[calc(50%-0.5rem)] lg:w-full animate-slide-in-up">
                              <div className="bg-white border rounded-xl px-2 py-2 gap-1 flex flex-col w-full border-gray-300 hover:shadow-md transition-shadow duration-200">
                                 <div className="flex flex-row justify-between items-center">
                                     <span className="text-xs md:text-sm lg:text-[12px] xl:text-[14px] font-medium text-gray-800">{account.name}</span>
                                     <DropdownAksi
                                         onEdit={() => handleOpenEditAccountModal(account)}
                                         onDelete={() => handleDeleteAccount(account.id)}
                                     />
                                 </div>
                                 <span className="text-xs md:text-sm lg:text-[12px] xl:text-[14px] font-semibold text-gray-900">
                                     Rp {Number(account.balance).toLocaleString('id-ID')}
                                 </span>
                              </div>
                          </div>
                      ))
                  ) : (
                      <p className="p-2 text-xs md:text-sm text-gray-500 w-full text-center">Belum ada akun.</p>
                  )}
                  <button
                      onClick={handleOpenAddAccountModal}
                      className="bg-gray-100 text-gray-400 text-sm px-5 py-2 rounded-xl hover:bg-gray-200 hover:text-gray-500 transition-colors duration-200 w-full sm:w-[calc(50%-0.5rem)] lg:w-full"
                  >
                      + Tambah Akun
                  </button>
              </div>
          </div>
        </div>
        
        {/* Bagian Tengah - Transaksi & Pendapatan */}
        <div className="w-full lg:w-3/6 px-0 lg:px-2 mt-8 lg:mt-0">
          <div className="flex flex-col">
            <h1 className="bg-gray-300 rounded-md text-base md:text-xl lg:text-2xl font-bold text-white mx-0 sm:mx-2 px-2 py-1 mb-2 w-full sm:w-5/6 animate-fade-in-down">
              Transaksi
            </h1>
            <div className="border-b border-gray-200 dark:border-gray-700 mb-2 animate-slide-in-up" style={{ animationDelay: '0.4s' }}>
              <ul className="flex flex-wrap -mb-px text-sm font-medium text-center text-gray-500 dark:text-gray-400">
                {["Terkini", "Harian","Bulanan","Tahunan"].map((tab) => (
                  <li key={tab} className="me-2">
                    <button
                      onClick={() => setSelectedTab(tab)}
                      className={`inline-flex items-center justify-center p-3 sm:p-4 border-b-2 rounded-t-lg hover:text-gray-600 hover:border-gray-300 dark:hover:text-gray-300 group transition-colors duration-200 text-sm sm:text-base ${
                        selectedTab === tab
                          ? "text-blue-600 border-blue-600 dark:text-blue-500 dark:border-blue-500"
                          : "border-transparent"
                      }`}
                    >
                      {tab}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
            {selectedTab === "Terkini" && (
              <Recent
                budgets={budgetsByMonth}
                akun={accounts}
                transactions={transactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
            {selectedTab === "Harian" && (
              <FilterTransaksi
                budgets={budgetsByMonth}
                akun={accounts}
                tab={"Harian"}
                transactions={transactions}
                setTransactions={setTransactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
            {selectedTab === "Bulanan" && (
              <FilterTransaksi
                budgets={budgetsByMonth}
                akun={accounts}
                tab={"Bulanan"}
                transactions={transactions}
                setTransactions={setTransactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
            {selectedTab === "Tahunan" && (
              <FilterTransaksi
                budgets={budgetsByMonth}
                akun={accounts}
                tab={"Tahunan"}
                transactions={transactions}
                setTransactions={setTransactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
          </div>
  
          <div className="flex flex-col mt-8 lg:mt-10">
              <h1 className="bg-gray-300 rounded-md text-base md:text-xl lg:text-2xl font-bold text-white mx-0 sm:mx-2 px-2 py-1 mb-2 w-full sm:w-5/6 animate-fade-in-down">
                  Pendapatan
              </h1>
              <div className="border-b border-gray-200 dark:border-gray-700 mb-2 animate-slide-in-up" style={{ animationDelay: '0.5s' }}>
                  <ul className="flex flex-wrap -mb-px text-sm font-medium text-center text-gray-500 dark:text-gray-400">
                  {["Terkini", "Harian", "Bulanan", "Tahunan"].map((tab) => (
                      <li key={tab} className="me-2">
                      <button
                          onClick={() => setSelectedTabPendapatan(tab)}
                          className={`inline-flex items-center justify-center p-3 sm:p-4 border-b-2 rounded-t-lg hover:text-gray-600 hover:border-gray-300 dark:hover:text-gray-300 group transition-colors duration-200 text-sm sm:text-base ${
                          selectedTabPendapatan === tab
                              ? "text-blue-600 border-blue-600 dark:text-blue-500 dark:border-blue-500"
                              : "border-transparent"
                          }`}
                      >
                          {tab}
                      </button>
                      </li>
                  ))}
                  </ul>
              </div>
              {selectedTabPendapatan === "Terkini" && (
                  <Recent
                  budgets={null}
                  akun={accounts}
                  transactions={pendapatan}
                  onDelete={handleDeleteTransaction}
                  onEdit={handleOpenEditTransactionModal}
                  type={"Akun"}
                  />
              )}
              {selectedTabPendapatan === "Harian" && (
                  <FilterTransaksi
                  budgets={null}
                  akun={accounts}
                  tab={"Harian"}
                  transactions={pendapatan}
                  setTransactions={setPendapatan}
                  type={"Akun"}
                  onDelete={handleDeleteTransaction}
                  />
              )}
              {selectedTabPendapatan === "Bulanan" && (
                  <FilterTransaksi
                  budgets={null}
                  akun={accounts}
                  tab={"Bulanan"}
                  transactions={pendapatan}
                  setTransactions={setPendapatan}
                  type={"Akun"}
                  onDelete={handleDeleteTransaction}
                  />
              )}
              {selectedTabPendapatan === "Tahunan" && (
                  <FilterTransaksi
                  budgets={null}
                  akun={accounts}
                  tab={"Tahunan"}
                  transactions={pendapatan}
                  setTransactions={setPendapatan}
                  type={"Akun"}
                  onDelete={handleDeleteTransaction}
                  />
              )}
          </div>
        </div>
  
        {/* Bagian Kanan - Grafik */}
        <div className="w-full lg:w-2/6 px-0 sm:px-2 mt-8 lg:mt-0">
          <div className="flex flex-col">
            <h1 className="bg-gray-300 rounded-md text-base md:text-xl lg:text-2xl font-bold text-white mx-0 sm:mx-2 px-2 py-1 mb-2 w-full animate-fade-in-down">
              Graph Pengeluaran
            </h1>
            <ChartKeuangan transactions={transactions} type={"expense"} />
          </div>
          <div className="flex flex-col mt-5">
            <h1 className="bg-gray-300 rounded-md text-base md:text-xl lg:text-2xl font-bold text-white mx-0 sm:mx-2 px-2 py-1 mb-2 w-full animate-fade-in-down">
              Graph Pendapatan
            </h1>
            <ChartKeuangan transactions={pendapatan} type={"income"} />
          </div>
        </div>
      </div>
          
      {/* Modal Components */}
      <ModalAddMonth
        isOpen={isModalAddMonthOpen}
        onClose={() => setIsModalAddMonthOpen(false)}
        onAddMonth={handleAddMonth}
      />
      <ModalTransaksi
        isOpen={isTransactionModalOpen}
        onClose={() => setTransactionModalOpen(false)}
        onSave={handleSaveTransaction}
        initialData={selectedTransaction}
        categories={categories}
        accounts={accounts}
      />
      <ModalKategori
        isOpen={isCategoryModalOpen}
        onClose={() => setCategoryModalOpen(false)}
        onSave={handleSaveCategory}
        initialData={selectedCategory}
      />
      <ModalAkun 
        isOpen={isAccountModalOpen}
        onClose={() => setAccountModalOpen(false)}
        onSave={handleSaveAccount}
        initialData={selectedAccount}
      />
      {isIconPickerOpen && (
        <IconPickerModal
            onClose={() => setIconPickerOpen(false)}
            onSelect={handleIconSelect}
        />
    )}
    </div>
  );
}

export default App;
