import ModalAddMonth from "./ModalAddMonth";
import React, { useEffect, useState, useCallback } from "react";
import IconPickerModal from "./IconPickerModal";
import Recent from './RecentTransaction';
import FilterTransaksi from './FilterTransaksi';
import ChartKeuangan from './ChartKeuangan';
import ModalTransaksi from './ModalTransaksi';
import ModalKategori from './ModalKategori';
import ModalAkun from './ModalAkun';
import { useNavigate } from 'react-router-dom';
import DropdownAksi from "./DropdownButton";
import { 
  getTransactions, getBudgets, getCategories, deleteTransaction, 
  deleteCategory, setBudget,createTransaction, updateTransaction, 
  createCategory, updateCategory, createAccount, getAccounts, 
  updateAccount, deleteAccount
} from './apiservice';

function App() {
  const navigate = useNavigate();
  const [accounts, setAccounts] = useState([]);
  const [budgetsByMonth, setBudgetsByMonth] = useState({});
  const [transactions, setTransactions] = useState([]); // Pengeluaran
  const [pendapatan, setPendapatan] = useState([]); // Pendapatan
  const [categories, setCategories] = useState([]);
  const [isTransactionModalOpen, setTransactionModalOpen] = useState(false);
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [isCategoryModalOpen, setCategoryModalOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [editingBudgetId, setEditingBudgetId] = useState(null);
  const [editingBudgetAmount, setEditingBudgetAmount] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isModalAddMonthOpen, setIsModalAddMonthOpen] = useState(false);
  const [selectedTab, setSelectedTab] = useState("Terkini");
  const [selectedTabPendapatan, setSelectedTabPendapatan] = useState("Terkini");
  const [isIconPickerOpen, setIconPickerOpen] = useState(false);
  const [iconTargetCategory, setIconTargetCategory] = useState(null);

  const [selectedMonth, setSelectedMonth] = useState(() => {
    const d = new Date();
    return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}`;
  });

  const fetchData = useCallback(async () => {
    setIsLoading(true);
    try {
      const [year, month] = selectedMonth.split('-');
      
      const [accountData, budgetData, transactionData, categoryData] = await Promise.all([
        getAccounts(),
        getBudgets(year, month),
        getTransactions(), // Mengambil semua transaksi
        getCategories()
      ]);
      setAccounts(accountData);
      
      const budgetMap = { [selectedMonth]: budgetData };
      setBudgetsByMonth(budgetMap);
      
      // Filter transaksi di sini, setelah mendapatkan semua data
      const expenseTxs = transactionData.filter(tx => tx.type === 'expense');
      const incomeTxs = transactionData.filter(tx => tx.type === 'income');
      setTransactions(expenseTxs);
      setPendapatan(incomeTxs);
      
      setCategories(categoryData);

    } catch (error) {
      console.error("Gagal mengambil data:", error);
      // Di sini Anda bisa menambahkan logika redirect ke halaman login jika token tidak valid
      // navigate('/'); // Contoh redirect jika error autentikasi
    } finally {
      setIsLoading(false);
    }
  }, [selectedMonth]); // HANYA PANGGIL FETCDATA JIKA BULAN BERUBAH

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  function handleAddMonth(newMonth) {
    if (budgetsByMonth[newMonth]) {
      alert("Maaf, bulan sudah ada!");
      return;
    }
    // Tambahkan bulan baru dengan data kosong, lalu pilih bulan tersebut
    setBudgetsByMonth(prev => ({
      ...prev,
      [newMonth]: []
    }));
    setSelectedMonth(newMonth); // Ini akan memicu fetchData
    setIsModalAddMonthOpen(false);
  }

  // **** UPDATE: handleSaveBudget ****
  const handleSaveBudget = async (categoryId, amount) => {
    const [year, month] = selectedMonth.split('-');
    try {
      const parsedAmount = parseFloat(amount) || 0;
      await setBudget({
        category_id: categoryId,
        amount: parsedAmount,
        year: parseInt(year),
        month: parseInt(month),
      });

      // Update state lokal secara langsung
      setBudgetsByMonth(prevBudgets => {
        const currentMonthBudgets = prevBudgets[selectedMonth] || [];
        const updatedBudgets = currentMonthBudgets.map(b =>
          b.category_id === categoryId ? { ...b, amount: parsedAmount } : b
        );
        return {
          ...prevBudgets,
          [selectedMonth]: updatedBudgets
        };
      });

      alert('Budget berhasil disimpan.');
      // fetchData(); // Tidak perlu fetchData() jika update state lokal sudah dilakukan dengan benar
    } catch (error) {
      console.error("Gagal simpan budget:", error);
      alert(error.message);
    } finally {
      setEditingBudgetId(null);
    }
  };

  // **** UPDATE: handleDeleteTransaction ****
  const handleDeleteTransaction = async (transactionId) => {
    if (window.confirm("Anda yakin ingin menghapus transaksi ini?")) {
      try {
        await deleteTransaction(transactionId);
        // Update state secara manual agar UI cepat merespons
        setTransactions(prev => prev.filter(tx => tx.id !== transactionId));
        setPendapatan(prev => prev.filter(p => p.id !== transactionId));
        
        // Opsional: Jika penghapusan transaksi mempengaruhi total 'used' di budget, Anda mungkin perlu mengupdate 'used' di budgetsByMonth
        // Ini akan lebih kompleks karena Anda perlu tahu detail transaksi yang dihapus
        // Untuk saat ini, asumsikan fetchData() akan menangani re-kalkulasi 'used' di budget
        fetchData(); // Karena perubahan transaksi memengaruhi "used" budget dan saldo akun, kita masih perlu fetchData
        alert('Transaksi berhasil dihapus.');
      } catch (error) {
        console.error("Gagal menghapus transaksi:", error);
        alert(error.message);
      }
    }
  };
  
  // **** UPDATE: handleHapusKategori ****
  const handleHapusKategori = async (categoryId) => {
    if (window.confirm("Yakin ingin menghapus kategori ini?")) {
      try {
        await deleteCategory(categoryId);
        // Update state categories secara langsung
        setCategories(prev => prev.filter(cat => cat.id !== categoryId));
        
        // Hapus juga dari budgetsByMonth jika ada
        setBudgetsByMonth(prev => {
          const newBudgets = { ...prev };
          for (const month in newBudgets) {
            newBudgets[month] = newBudgets[month].filter(b => b.category_id !== categoryId);
          }
          return newBudgets;
        });
        
        // Karena ini juga bisa mempengaruhi transaksi yang sudah ada, lebih aman untuk fetchData
        fetchData(); 
        alert('Kategori berhasil dihapus.');
      } catch (error) {
        console.error("Gagal hapus kategori:", error);
        alert(error.message);
      }
    }
  };

  const handleOpenEditTransactionModal = (transaction) => {
    setSelectedTransaction(transaction);
    setTransactionModalOpen(true);
  };

  // **** UPDATE: handleSaveTransaction ****
  const handleSaveTransaction = async (data) => {
    try {
      let savedTransaction;
      if (data.id) {
        const { type, ...updateData } = data;
        await updateTransaction(data.id, updateData);
        savedTransaction = { ...data }; // Gunakan data yang dikirim karena API tidak mengembalikan seluruh objek
      } else {
        savedTransaction = await createTransaction(data); // Asumsi API mengembalikan objek transaksi yang dibuat
      }

      // Update state transaksi secara langsung
      if (savedTransaction.type === 'expense') {
        setTransactions(prev => {
          if (data.id) {
            return prev.map(tx => tx.id === savedTransaction.id ? savedTransaction : tx);
          } else {
            return [...prev, savedTransaction];
          }
        });
      } else if (savedTransaction.type === 'income') {
        setPendapatan(prev => {
          if (data.id) {
            return prev.map(tx => tx.id === savedTransaction.id ? savedTransaction : tx);
          } else {
            return [...prev, savedTransaction];
          }
        });
      }

      setTransactionModalOpen(false);
      // Panggil fetchData untuk memastikan semua data (terutama saldo akun dan budget 'used') terupdate
      // Karena perubahan transaksi dapat mempengaruhi beberapa state lain yang perhitungannya kompleks
      fetchData(); 
      alert('Transaksi berhasil disimpan!');
    } catch (error) {
      console.error('Gagal menyimpan transaksi:', error);
      alert(error.message);
    }
  };

  const handleOpenAddCategoryModal = () => {
    setSelectedCategory(null);
    setCategoryModalOpen(true);
  };

  const handleOpenEditCategoryModal = (category) => {
    setSelectedCategory({ id: category.category_id, name: category.category_name });
    setCategoryModalOpen(true);
  };

  // **** UPDATE: handleSaveCategory ****
  const handleSaveCategory = async (data) => {
    try {
      if (data.id) {
        await updateCategory(data.id, { name: data.name });
        // Update state categories
        setCategories(prev => 
          prev.map(cat => cat.id === data.id ? { ...cat, name: data.name } : cat)
        );
        // Update category name in budgetsByMonth if exists
        setBudgetsByMonth(prevBudgets => {
            const currentMonthBudgets = prevBudgets[selectedMonth] || [];
            const updatedBudgets = currentMonthBudgets.map(b =>
                b.category_id === data.id ? { ...b, category_name: data.name } : b
            );
            return {
                ...prevBudgets,
                [selectedMonth]: updatedBudgets
            };
        });
        alert('Nama kategori berhasil diupdate!');
      } else {
        const newCategory = await createCategory({ name: data.name });
        // Update state categories
        setCategories(prev => [...prev, newCategory]);

        if (data.amount > 0) {
          const [year, month] = selectedMonth.split('-');
          const newBudget = await setBudget({ // Asumsi setBudget mengembalikan data budget yang dibuat/diupdate
            category_id: newCategory.id,
            amount: data.amount,
            year: parseInt(year),
            month: parseInt(month),
          });
          // Update state budgetsByMonth
          setBudgetsByMonth(prevBudgets => {
            const currentMonthBudgets = prevBudgets[selectedMonth] || [];
            return {
              ...prevBudgets,
              [selectedMonth]: [...currentMonthBudgets, { ...newBudget, category_name: newCategory.name }]
            };
          });
        }
        alert('Kategori baru berhasil ditambahkan!');
      }
      setCategoryModalOpen(false);
      // fetchData(); // Tidak perlu fetchData jika update state lokal sudah cukup
    } catch (error) {
      console.error('Gagal menyimpan kategori:', error);
      alert(error.message);
    }
  };

  const [isAccountModalOpen, setAccountModalOpen] = useState(false);

  const handleOpenAddAccountModal = () => setAccountModalOpen(true);

  // **** UPDATE: handleSaveAccount ****
  const handleSaveAccount = async (data) => {
    try {
      let savedAccount;
      if (data.id) {
        await updateAccount(data.id, data);
        savedAccount = { ...data }; // Asumsi API tidak mengembalikan seluruh objek
      } else {
        savedAccount = await createAccount(data); // Asumsi API mengembalikan objek akun yang dibuat
      }
      
      // Update state accounts
      setAccounts(prev => {
        if (data.id) {
          return prev.map(acc => acc.id === savedAccount.id ? savedAccount : acc);
        } else {
          return [...prev, savedAccount];
        }
      });
      setAccountModalOpen(false);
      // fetchData(); // fetchData masih mungkin diperlukan karena perubahan akun bisa memengaruhi transaksi
      alert('Akun berhasil disimpan!');
    } catch (error) {
      alert(error.message);
    }
  };

  // **** UPDATE: handleDeleteAccount ****
  const handleDeleteAccount = async (accountId) => {
    if(window.confirm('Yakin ingin menghapus akun ini? Aksi ini tidak bisa dibatalkan.')){
      try {
        await deleteAccount(accountId);
        // Update state accounts secara langsung
        setAccounts(prev => prev.filter(acc => acc.id !== accountId));
        // Karena penghapusan akun mungkin memengaruhi transaksi yang terkait, lebih aman fetchData
        fetchData();
        alert('Akun berhasil dihapus.');
      } catch (error) {
        alert(error.message);
      }
    }
  }

  const handleOpenEditAccountModal = (account) => {
    setSelectedAccount(account);
    setAccountModalOpen(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('userToken');
    navigate('/');
  };

  const CircularProgress = ({ percentage }) => {
    const pct = Number(percentage);
    const radius = 20;
    const stroke = 4;
    const normalized = radius - stroke / 2;
    const circumference = 2 * Math.PI * normalized;
  
    const clamped = Math.min(pct, 100);
    const offset = circumference * (1 - clamped / 100);
  
    const color =
      pct > 99 ? 'stroke-red-500'
      : pct > 50 ? 'stroke-yellow-400'
      : pct > 0 ? 'stroke-green-400'
      : 'stroke-transparent';
  
    return (
      <svg className="w-5 h-5" viewBox="0 0 48 48">
        <circle
          className="stroke-gray-200"
          strokeWidth={stroke}
          fill="transparent"
          r={normalized}
          cx="24"
          cy="24"
        />
        <circle
          className={`transition-all duration-300 ${color}`}
          strokeWidth={stroke}
          fill="transparent"
          r={normalized}
          cx="24"
          cy="24"
          strokeDasharray={circumference}
          strokeDashoffset={offset}
          strokeLinecap="round"
        />
      </svg>
    );
  };

  // **** UPDATE: handleIconSelect ****
  const handleOpenIconPicker = (category) => {
    setIconTargetCategory(category);
    setIconPickerOpen(true);
  };

  const handleIconSelect = async (iconPath) => {
    if (!iconTargetCategory) return;
    try {
      await updateCategory(iconTargetCategory.category_id, { icon_path: iconPath });
      // Update state categories secara langsung
      setCategories(prev => 
        prev.map(cat => 
          cat.id === iconTargetCategory.category_id ? { ...cat, icon_path: iconPath } : cat
        )
      );
      // Update icon_path in budgetsByMonth if exists
        setBudgetsByMonth(prevBudgets => {
            const currentMonthBudgets = prevBudgets[selectedMonth] || [];
            const updatedBudgets = currentMonthBudgets.map(b =>
                b.category_id === iconTargetCategory.category_id ? { ...b, icon_path: iconPath } : b
            );
            return {
                ...prevBudgets,
                [selectedMonth]: updatedBudgets
            };
        });

      setIconPickerOpen(false);
      // fetchData(); // Tidak perlu fetchData jika update state lokal sudah cukup
    } catch (error) {
      console.error("Gagal update ikon:", error);
      alert(error.message);
    }
  };

  function formatMonth(monthStr) {
    const [year, month] = monthStr.split("-");
    const monthNames = [
      "Januari", "Februari", "Maret", "April", "Mei", "Juni",
      "Juli", "Agustus", "September", "Oktober", "November", "Desember"
    ];
    return `${monthNames[parseInt(month, 10) - 1]} ${year}`;
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="flex-col items-start justify-center min-h-screen bg-gray-200">
      {/* ... (bagian JSX lainnya sama) ... */}

      <div className="flex flex-row w-full bg-black shadow-md">
        <h1 className="px-5 py-4 text-xl font-bold text-white">=</h1>
      </div>
  
      <div className="w-full bg-gray-700 shadow-md text-center py-9 md:py-32 lg:py-32">
        <h1 className="font-semibold text-white mb-4 text-4xl md:text-6xl lg:text-7xl">
          DuitKu
        </h1>
        <button
          onClick={handleLogout}
          className="absolute top-4 right-4 bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded-lg shadow"
        >
          Logout
        </button>
      </div>
      <div className="flex justify-center mt-5">
        <div className="inline-flex rounded-full overflow-hidden shadow-md border border-gray-500">
          <button
            className="bg-gray-500 text-white px-6 py-2 text-sm md:text-base font-medium rounded-l-full"
          >
            Transaksi
          </button>
          <button
            onClick={() => navigate("/hutang")}
            className="bg-white text-gray-500 hover:bg-gray-100 px-6 py-2 text-sm md:text-base font-medium rounded-r-full"
          >
            Hutang 
          </button>
        </div>
      </div>
      <div className="py-10 px-10 flex flex-row bg-gray-200">
        <div className="flex flex-col w-1/6 mr-8">
          <div className="flex flex-col">
            <h1 className="bg-gray-500 rounded-md text-[13px] md:text-base lg:text-lg font-bold text-white px-2 w-3/4">
              Kategori Bulanan
            </h1>
            <div className="mb-4 py-2">
              <label htmlFor="month-select" className="text-sm font-semibold mr-2 text-gray-700">
                Pilih Bulan:
              </label>
              <select
                value={selectedMonth}
                onChange={(e) => {
                  const val = e.target.value;
                  if (val === "add_new") {
                    setIsModalAddMonthOpen(true);
                  } else {
                    setSelectedMonth(val);
                  }
                }}
              >
                {Object.keys(budgetsByMonth)
                  .sort((a, b) => new Date(a) - new Date(b))
                  .map((month) => (
                    <option key={month} value={month}>
                      {formatMonth(month)}
                    </option>
                  ))
                }
                <option value="add_new">+ Tambah Bulan</option>
              </select>
            </div>
            <div className="flex flex-wrap gap-3 py-1 w-full">
              {(budgetsByMonth[selectedMonth] || []).map((item) => {
                const budgetAmount = parseFloat(item.amount) || 0;
                const usedAmount = parseFloat(item.used) || 0;
                const percentage = budgetAmount > 0 ? (usedAmount / budgetAmount) * 100 : 0;
                const isEditing = editingBudgetId === item.category_id;
  
                return (
                  <div
                    key={item.category_id}
                    className="w-4/6 sm:w-4/6 lg:w-2/3"
                  >
                    <div className="bg-white border rounded-xl px-2 py-1 gap-1 flex flex-col w-full border-gray-300">
                      <div className="flex flex-row">
                        <div className="flex flex-wrap items-center w-5/6">
                          <div
                              className="w-8 h-8 rounded-full border bg-gray-200 flex items-center justify-center cursor-pointer mr-2 flex-shrink-0"
                              onClick={() => handleOpenIconPicker(item)}
                              title="Ubah Ikon"
                            >
                              {item.icon_path ? (
                                    <img src={item.icon_path} alt={item.category_name} className="w-full h-full object-cover rounded-full" />
                                ) : (
                                    <span className="text-gray-500 text-xl font-light">+</span>
                                )}
                            </div>
                            <span className="text-sm">{item.category_name}</span>
                        </div>
                        <DropdownAksi
                            onEdit={() => handleOpenEditCategoryModal(item)}
                            onDelete={() => handleHapusKategori(item.category_id)}
                        />
                      </div>
                      <div className="text-[11px] mt-1 w-fit relative group inline-block">
                          {isEditing ? (
                                <input
                                    type="number"
                                    value={editingBudgetAmount}
                                    onChange={(e) => setEditingBudgetAmount(e.target.value)}
                                    onBlur={() => handleSaveBudget(item.category_id, editingBudgetAmount)}
                                    onKeyDown={(e) => { if (e.key === 'Enter') handleSaveBudget(item.category_id, editingBudgetAmount) }}
                                    autoFocus
                                    className="w-24 border-b-2 border-blue-500 outline-none"
                                />
                            ) : (
                                <span onClick={() => {
                                    setEditingBudgetId(item.category_id);
                                    setEditingBudgetAmount(budgetAmount);
                                }}
                                className="cursor-pointer"
                                >
                                    Rp {budgetAmount.toLocaleString('id-ID')}
                                </span>
                            )}
                      </div>
                      <div className="flex flex-row gap-1 items-center relative w-fit group cursor-pointer">
                        <div className="text-[11px]">
                          {percentage.toFixed(0)}%
                        </div>
                        <CircularProgress percentage={percentage} />
                        <div className="absolute left-full top-1/2 ml-2 -translate-y-1/2
                            bg-gray-500 text-white text-[11px] px-1 py-1 rounded 
                            opacity-0 group-hover:opacity-100 transition-all duration-200 
                            whitespace-nowrap z-10
                            pointer-events-none">
                          Pemakaian
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
  
              <button
                      onClick={handleOpenAddCategoryModal}
                      className="bg-gray-100 text-gray-300 xl:text-[12px] 2xl:text-[15px] px-5 py-2 rounded hover:bg-gray-500 w-9/12"
                    >
                      + Tambah Kategori
                    </button>
            </div>
          </div>
          <h1 className="bg-gray-500 rounded-md text-[13px] md:text-base lg:text-lg font-bold text-white px-2 w-3/4">
            Daftar Akun
          </h1>
          <div className="flex flex-wrap gap-3 py-1 w-full">
              {accounts.length > 0 ? (
                  accounts.map(account => (
                      <div key={account.id} className="w-4/6 sm:w-4/6 lg:w-2/3">
                          <div className="bg-white border rounded-xl px-2 py-2 gap-1 flex flex-col w-full border-gray-300">
                             <div className="flex flex-row justify-between items-center">
                                 <span className="text-sm font-medium text-gray-800">{account.name}</span>
                                 <DropdownAksi
                                     onEdit={() => handleOpenEditAccountModal(account)}
                                     onDelete={() => handleDeleteAccount(account.id)}
                                 />
                             </div>
                             <span className="text-sm font-semibold text-gray-900">
                                 Rp {Number(account.balance).toLocaleString('id-ID')}
                             </span>
                          </div>
                      </div>
                      ))
              ) : (
                  <p className="p-2 text-sm text-gray-500">Belum ada akun.</p>
              )}
              <button
                  onClick={handleOpenAddAccountModal}
                  className="bg-gray-100 text-gray-400 text-sm px-5 py-2 rounded-xl hover:bg-gray-200 w-4/6 sm:w-4/6 lg:w-2/3"
              >
                  + Tambah Akun
              </button>
          </div>
        </div>
        <div className="w-3/6">
          <div className="flex flex-col">
            <h1 className="bg-gray-300 rounded-md text-lg md:2xl lg:text-3xl font-bold text-white mx-2 px-2 w-5/6">
              Transaksi
            </h1>
            <div className="border-b border-gray-200 dark:border-gray-700 mb-2">
              <ul className="flex flex-wrap -mb-px text-sm font-medium text-center text-gray-500 dark:text-gray-400">
                {["Terkini", "Harian","Bulanan","Tahunan"].map((tab) => (
                  <li key={tab} className="me-2">
                    <button
                      onClick={() => setSelectedTab(tab)}
                      className={`inline-flex items-center justify-center p-4 border-b-2 rounded-t-lg hover:text-gray-600 hover:border-gray-300 dark:hover:text-gray-300 group ${
                        selectedTab === tab
                          ? "text-blue-600 border-blue-600 dark:text-blue-500 dark:border-blue-500"
                          : "border-transparent"
                      }`}
                    >
                      {tab}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
            {selectedTab === "Terkini" && (
              <Recent
                budgets={budgetsByMonth}
                akun={null}
                transactions={transactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
            {selectedTab === "Harian" && (
              <FilterTransaksi
                budgets={budgetsByMonth}
                akun={null}
                tab={"Harian"}
                transactions={transactions}
                setTransactions={setTransactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
            {selectedTab === "Bulanan" && (
              <FilterTransaksi
                budgets={budgetsByMonth}
                akun={null}
                tab={"Bulanan"}
                transactions={transactions}
                setTransactions={setTransactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
            {selectedTab === "Tahunan" && (
              <FilterTransaksi
                budgets={budgetsByMonth}
                akun={null}
                tab={"Tahunan"}
                transactions={transactions}
                setTransactions={setTransactions}
                type={"Budget"}
                onDelete={handleDeleteTransaction}
                onEdit={handleOpenEditTransactionModal}
              />
            )}
          </div>
  
          <div className="flex flex-col mt-10">
              <h1 className="bg-gray-300 rounded-md text-lg md:2xl lg:text-3xl font-bold text-white mx-2 px-2 w-5/6">
                  Pendapatan
              </h1>
              <div className="border-b border-gray-200 dark:border-gray-700 mb-2">
                  <ul className="flex flex-wrap -mb-px text-sm font-medium text-center text-gray-500 dark:text-gray-400">
                  {["Terkini", "Harian", "Bulanan", "Tahunan"].map((tab) => (
                      <li key={tab} className="me-2">
                      <button
                          onClick={() => setSelectedTabPendapatan(tab)}
                          className={`inline-flex items-center justify-center p-4 border-b-2 rounded-t-lg hover:text-gray-600 hover:border-gray-300 dark:hover:text-gray-300 group ${
                          selectedTabPendapatan === tab
                              ? "text-blue-600 border-blue-600 dark:text-blue-500 dark:border-blue-500"
                              : "border-transparent"
                          }`}
                      >
                          {tab}
                      </button>
                      </li>
                  ))}
                  </ul>
              </div>
              {selectedTabPendapatan === "Terkini" && (
                  <Recent
                  budgets={null}
                  akun={null}
                  transactions={pendapatan}
                  onDelete={handleDeleteTransaction}
                  onEdit={handleOpenEditTransactionModal}
                  type={"Akun"}
                  />
              )}
              {selectedTabPendapatan === "Harian" && (
                  <FilterTransaksi
                  budgets={null}
                  akun={null}
                  tab={"Harian"}
                  transactions={pendapatan}
                  setTransactions={setPendapatan}
                  type={"Akun"}
                  onDelete={handleDeleteTransaction}
                  />
              )}
              {selectedTabPendapatan === "Bulanan" && (
                  <FilterTransaksi
                  budgets={null}
                  akun={null}
                  tab={"Bulanan"}
                  transactions={pendapatan}
                  setTransactions={setPendapatan}
                  type={"Akun"}
                  onDelete={handleDeleteTransaction}
                  />
              )}
              {selectedTabPendapatan === "Tahunan" && (
                  <FilterTransaksi
                  budgets={null}
                  akun={null}
                  tab={"Tahunan"}
                  transactions={pendapatan}
                  setTransactions={setPendapatan}
                  type={"Akun"}
                  onDelete={handleDeleteTransaction}
                  />
              )}
          </div>
        </div>
  
        <div className="w-2/6 px-2">
          <div className="flex flex-col">
            <h1 className="bg-gray-300 rounded-md text-[13px] md:text-base lg:text-lg font-bold text-white mx-2 px-2 w-full">
              Graph Pengeluaran
            </h1>
            <ChartKeuangan transactions={transactions} type={"expense"} />
          </div>
          <div className="flex flex-col mt-5">
            <h1 className="bg-gray-300 rounded-md text-[13px] md:text-base lg:text-lg font-bold text-white mx-2 px-2 w-full">
              Graph Pendapatan
            </h1>
            <ChartKeuangan transactions={pendapatan} type={"income"} />
          </div>
        </div>
      </div>
          
      <ModalAddMonth
            isOpen={isModalAddMonthOpen}
            onClose={() => setIsModalAddMonthOpen(false)}
            onAddMonth={handleAddMonth}
      />
      <ModalTransaksi
        isOpen={isTransactionModalOpen}
        onClose={() => setTransactionModalOpen(false)}
        onSave={handleSaveTransaction}
        initialData={selectedTransaction}
        categories={categories}
        accounts={accounts}
      />
      <ModalKategori
        isOpen={isCategoryModalOpen}
        onClose={() => setCategoryModalOpen(false)}
        onSave={handleSaveCategory}
        initialData={selectedCategory}
      />
      <ModalAkun 
        isOpen={isAccountModalOpen}
        onClose={() => setAccountModalOpen(false)}
        onSave={handleSaveAccount}
        initialData={selectedAccount}
      />
      {isIconPickerOpen && (
        <IconPickerModal
            onClose={() => setIconPickerOpen(false)}
            onSelect={handleIconSelect}
        />
    )}
    </div>
  );
}

export default App;
