import React, { useState } from 'react';

// Komponen Modalnotifikasi yang Anda berikan (dengan sedikit penyesuaian kecil untuk konsistensi)
const Modalnotifikasi = ({ isOpen, onClose, onConfirm, title, message, type = 'info', showCancelButton = false }) => {
  if (!isOpen) return null; // Jika tidak terbuka, jangan render apa pun

  // Kelas CSS untuk styling berdasarkan tipe modal
  let headerBgClass = 'bg-gray-500';
  let titleColorClass = 'text-white';
  let messageColorClass = 'text-gray-800';
  let confirmButtonBgClass = 'bg-blue-600 hover:bg-blue-700'; // Default untuk info/confirm

  switch (type) {
    case 'success':
      headerBgClass = 'bg-green-500';
      confirmButtonBgClass = 'bg-green-600 hover:bg-green-700';
      break;
    case 'error':
      headerBgClass = 'bg-red-500';
      confirmButtonBgClass = 'bg-red-600 hover:bg-red-700';
      messageColorClass = 'text-red-700'; // Pesan error bisa berwarna merah
      break;
    case 'warning':
      headerBgClass = 'bg-yellow-500';
      confirmButtonBgClass = 'bg-yellow-600 hover:bg-yellow-700';
      messageColorClass = 'text-yellow-800'; // Pesan warning bisa berwarna kuning gelap
      break;
    case 'confirm': // Tipe baru untuk konfirmasi
      headerBgClass = 'bg-blue-500';
      confirmButtonBgClass = 'bg-blue-600 hover:bg-blue-700';
      break;
    default: // info
      // Kelas default sudah diatur
      break;
  }

  return (
    // Overlay modal: menutupi seluruh layar dengan latar belakang semi-transparan
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 font-sans">
      {/* Konten modal: kotak putih di tengah */}
      <div className="bg-white rounded-lg shadow-xl w-full max-w-sm overflow-hidden transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
        {/* Header Modal */}
        <div className={`${headerBgClass} px-6 py-3 rounded-t-lg`}>
          <h3 className={`text-lg font-semibold ${titleColorClass}`}>{title}</h3>
        </div>

        {/* Body Modal */}
        <div className="p-6">
          <p className={`text-sm ${messageColorClass} mb-6`}>{message}</p>
          {/* Tombol aksi */}
          <div className="flex justify-end space-x-3">
            {/* Tombol Batal (opsional) */}
            {showCancelButton && (
              <button
                onClick={onClose} // Tombol Batal hanya memanggil onClose untuk menutup modal
                className="px-6 py-2 rounded-md font-semibold text-gray-700 bg-gray-200 hover:bg-gray-300 transition-colors duration-200 shadow-sm focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-opacity-75"
              >
                Batal
              </button>
            )}
            {/* Tombol Konfirmasi/OK */}
            <button
              onClick={() => {
                if (onConfirm) {
                  onConfirm(); // Panggil onConfirm. Komponen App sekarang akan menangani penutupan modal ini.
                } else {
                  onClose(); // Untuk notifikasi sederhana, tutup segera.
                }
              }}
              className={`px-6 py-2 rounded-md font-semibold text-white ${confirmButtonBgClass} transition-colors duration-200 shadow-md focus:outline-none focus:ring-2 focus:ring-opacity-75 ${type === 'error' ? 'focus:ring-red-400' : 'focus:ring-blue-400'}`}
            >
              {showCancelButton ? 'Ya' : 'OK'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Komponen App utama yang akan menggunakan Modalnotifikasi
const App = () => {
  // State untuk mengontrol visibilitas modal
  const [isInfoModalOpen, setIsInfoModalOpen] = useState(false);
  const [isConfirmModalOpen, setIsConfirmModalOpen] = useState(false);
  const [isSuccessModalOpen, setIsSuccessModalOpen] = useState(false);
  const [isErrorModalOpen, setIsErrorModalOpen] = useState(false);
  const [isWarningModalOpen, setIsWarningModalOpen] = useState(false);

  // State untuk melacak hasil konfirmasi
  const [confirmationResult, setConfirmationResult] = useState('');

  // Handler untuk membuka modal
  const openInfoModal = () => setIsInfoModalOpen(true);
  const openConfirmModal = () => setIsConfirmModalOpen(true);
  const openSuccessModal = () => setIsSuccessModalOpen(true);
  const openErrorModal = () => setIsErrorModalOpen(true);
  const openWarningModal = () => setIsWarningModalOpen(true);

  // Handler untuk menutup modal (digunakan oleh onClose prop)
  const closeInfoModal = () => setIsInfoModalOpen(false);
  const closeConfirmModal = () => setIsConfirmModalOpen(false);
  const closeSuccessModal = () => setIsSuccessModalOpen(false);
  const closeErrorModal = () => setIsErrorModalOpen(false);
  const closeWarningModal = () => setIsWarningModalOpen(false);

  // Handler untuk konfirmasi (digunakan oleh onConfirm prop)
  const handleConfirmAction = () => {
    // Lakukan sesuatu setelah pengguna mengkonfirmasi
    setConfirmationResult('Anda mengkonfirmasi tindakan!');
    console.log('Tindakan dikonfirmasi!');
    closeConfirmModal(); // PENTING: Tutup modal setelah tindakan dikonfirmasi
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4 font-sans">
      <h1 className="text-3xl font-bold mb-8 text-gray-800">Contoh Penggunaan Modal Notifikasi</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {/* Tombol untuk membuka modal Info */}
        <button
          onClick={openInfoModal}
          className="px-6 py-3 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75 transition duration-300 ease-in-out"
        >
          Tampilkan Modal Info
        </button>

        {/* Tombol untuk membuka modal Konfirmasi */}
        <button
          onClick={openConfirmModal}
          className="px-6 py-3 bg-indigo-500 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-600 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:ring-opacity-75 transition duration-300 ease-in-out"
        >
          Tampilkan Modal Konfirmasi
        </button>

        {/* Tombol untuk membuka modal Sukses */}
        <button
          onClick={openSuccessModal}
          className="px-6 py-3 bg-green-500 text-white font-semibold rounded-lg shadow-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-green-400 focus:ring-opacity-75 transition duration-300 ease-in-out"
        >
          Tampilkan Modal Sukses
        </button>

        {/* Tombol untuk membuka modal Error */}
        <button
          onClick={openErrorModal}
          className="px-6 py-3 bg-red-500 text-white font-semibold rounded-lg shadow-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-opacity-75 transition duration-300 ease-in-out"
        >
          Tampilkan Modal Error
        </button>

        {/* Tombol untuk membuka modal Warning */}
        <button
          onClick={openWarningModal}
          className="px-6 py-3 bg-yellow-500 text-white font-semibold rounded-lg shadow-md hover:bg-yellow-600 focus:outline-none focus:ring-2 focus:ring-yellow-400 focus:ring-opacity-75 transition duration-300 ease-in-out"
        >
          Tampilkan Modal Peringatan
        </button>
      </div>

      {/* Menampilkan hasil konfirmasi */}
      {confirmationResult && (
        <p className="mt-8 text-lg font-medium text-green-700 p-3 bg-green-100 rounded-md shadow-sm">
          {confirmationResult}
        </p>
      )}

      {/* Instance Modalnotifikasi untuk Info */}
      <Modalnotifikasi
        isOpen={isInfoModalOpen}
        onClose={closeInfoModal}
        title="Informasi Penting"
        message="Ini adalah pesan informasi umum. Tekan OK untuk menutup."
        type="info"
      />

      {/* Instance Modalnotifikasi untuk Konfirmasi */}
      <Modalnotifikasi
        isOpen={isConfirmModalOpen}
        onClose={closeConfirmModal}
        onConfirm={handleConfirmAction} // Ini yang akan dipanggil saat OK ditekan
        title="Konfirmasi Tindakan"
        message="Apakah Anda yakin ingin melanjutkan tindakan ini? Ini tidak dapat dibatalkan."
        type="confirm"
        showCancelButton={true} // Tampilkan tombol Batal
      />

      {/* Instance Modalnotifikasi untuk Sukses */}
      <Modalnotifikasi
        isOpen={isSuccessModalOpen}
        onClose={closeSuccessModal}
        title="Berhasil!"
        message="Operasi Anda telah berhasil diselesaikan."
        type="success"
      />

      {/* Instance Modalnotifikasi untuk Error */}
      <Modalnotifikasi
        isOpen={isErrorModalOpen}
        onClose={closeErrorModal}
        title="Terjadi Kesalahan"
        message="Maaf, terjadi kesalahan saat memproses permintaan Anda. Silakan coba lagi."
        type="error"
      />

      {/* Instance Modalnotifikasi untuk Warning */}
      <Modalnotifikasi
        isOpen={isWarningModalOpen}
        onClose={closeWarningModal}
        title="Peringatan!"
        message="Ada beberapa masalah yang perlu Anda perhatikan sebelum melanjutkan."
        type="warning"
      />
    </div>
  );
};

export default App;
