import React, { useState, useEffect } from 'react';

// Modal ini sekarang hanya punya mode 'add' dan 'edit'
const ModalKategori = ({ isOpen, onClose, onSave, initialData, accounts = [], mode = 'add' }) => {
  const [formData, setFormData] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    if (isOpen) {
      setError('');
      // Reset form berdasarkan mode
      if (mode === 'add') {
        setFormData({ name: '', amount: '', account_id: '' });
      } else if (mode === 'edit') {
        setFormData({ name: initialData?.name || '' });
      }
    }
  }, [isOpen, mode, initialData]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (mode === 'add') {
      if (parseFloat(formData.amount) > 0 && !formData.account_id) {
        setError('Pilih akun untuk alokasi dana awal.');
        return;
      }
      onSave({ name: formData.name, amount: formData.amount, account_id: formData.account_id });
    } else if (mode === 'edit') {
      onSave({ id: initialData.id, name: formData.name });
    }
  };

  if (!isOpen) return null;

  const getTitle = () => {
    return mode === 'add' ? 'Tambah Kategori Baru' : 'Edit Kategori';
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-lg font-semibold mb-4">{getTitle()}</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          
          <div>
            <label>Nama Kategori</label>
            <input type="text" name="name" value={formData.name || ''} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md"/>
          </div>

          {/* Tampilkan field alokasi dana HANYA saat mode 'add' */}
          {mode === 'add' && (
            <>
              <div>
                <label>Nominal Budget Awal (Opsional)</label>
                <input type="number" name="amount" value={formData.amount || ''} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md" placeholder="0"/>
              </div>
              {/* Muncul jika nominal diisi */}
              {parseFloat(formData.amount) > 0 && (
                <div>
                  <label>Alokasikan Dana dari Akun</label>
                  <select name="account_id" value={formData.account_id || ''} onChange={handleChange} required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md">
                    <option value="">Pilih Akun...</option>
                    {accounts.map(acc => <option key={acc.id} value={acc.id}>{acc.name}</option>)}
                  </select>
                </div>
              )}
            </>
          )}
          
          {error && <p className="text-sm text-red-600">{error}</p>}
          <div className="flex justify-end space-x-2 pt-2">
            <button className='bg-gray-200 px-4 py-2 rounded-md' type="button" onClick={onClose}>Batal</button>
            <button className='bg-blue-500 text-white px-4 py-2 rounded-md' type="submit">Simpan</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ModalKategori;
