// ModalKategori.js
import React, { useState, useEffect } from 'react';

// Modal ini sekarang hanya punya mode 'add' dan 'edit'
const ModalKategori = ({ isOpen, onClose, onSave, initialData, accounts = [], mode = 'add' }) => {
  const [formData, setFormData] = useState({});
  const [error, setError] = useState('');

  useEffect(() => {
    if (isOpen) {
      setError(''); // Reset error setiap kali modal dibuka
      // Reset form berdasarkan mode
      if (mode === 'add') {
        setFormData({ name: '', amount: '', account_id: '' });
      } else if (mode === 'edit') {
        setFormData({ name: initialData?.name || '' });
      }
    }
  }, [isOpen, mode, initialData]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError(''); // Bersihkan error sebelum validasi baru

    if (mode === 'add') {
      // Validasi nama kategori
      if (!formData.name || formData.name.trim() === '') {
        setError('Nama kategori tidak boleh kosong.');
        return;
      }

      // Pastikan amount adalah angka yang valid dan tidak negatif
      const parsedAmount = parseFloat(formData.amount);
      if (isNaN(parsedAmount) || parsedAmount < 0) {
        setError('Nominal Budget Awal harus berupa angka positif atau nol.');
        return;
      }

      // Jika nominal diisi (lebih dari 0), pastikan akun dipilih
      if (parsedAmount > 0 && !formData.account_id) {
        setError('Pilih akun untuk alokasi dana awal jika nominal diisi.');
        return;
      }
      
      // Kirim data ke parent component
      onSave({ 
        name: formData.name.trim(), 
        amount: parsedAmount, // Kirim sebagai angka float
        account_id: formData.account_id 
      });
      onClose(); // Tutup modal setelah berhasil disimpan
    } else if (mode === 'edit') {
      // Validasi nama kategori untuk mode edit
      if (!formData.name || formData.name.trim() === '') {
        setError('Nama kategori tidak boleh kosong.');
        return;
      }
      onSave({ id: initialData.id, name: formData.name.trim() });
      onClose(); // Tutup modal setelah berhasil disimpan
    }
  };

  if (!isOpen) return null;

  const getTitle = () => {
    return mode === 'add' ? 'Tambah Kategori Baru' : 'Edit Kategori';
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-lg font-semibold mb-4">{getTitle()}</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700">Nama Kategori</label>
            <input 
              type="text" 
              id="name"
              name="name" 
              value={formData.name || ''} 
              onChange={handleChange} 
              required 
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>

          {/* Tampilkan field alokasi dana HANYA saat mode 'add' */}
          {mode === 'add' && (
            <>
              <div>
                <label htmlFor="amount" className="block text-sm font-medium text-gray-700">Nominal Budget Awal (Opsional)</label>
                <input 
                  type="number" 
                  id="amount"
                  name="amount" 
                  value={formData.amount || ''} 
                  onChange={handleChange} 
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent" 
                  placeholder="0"
                  min="0" // Pastikan input tidak negatif
                />
              </div>
              {/* Muncul jika nominal diisi (atau jika amount > 0) */}
              {parseFloat(formData.amount) > 0 && (
                <div>
                  <label htmlFor="account_id" className="block text-sm font-medium text-gray-700">Alokasikan Dana dari Akun</label>
                  <select 
                    id="account_id"
                    name="account_id" 
                    value={formData.account_id || ''} 
                    onChange={handleChange} 
                    required 
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="">Pilih Akun...</option>
                    {accounts.map(acc => <option key={acc.id} value={acc.id}>{acc.name}</option>)}
                  </select>
                </div>
              )}
            </>
          )}
          
          {error && <p className="text-sm text-red-600">{error}</p>}
          <div className="flex justify-end space-x-2 pt-2">
            <button 
              className='bg-gray-200 px-4 py-2 rounded-md hover:bg-gray-300 transition-colors duration-200' 
              type="button" 
              onClick={onClose}
            >
              Batal
            </button>
            <button 
              className='bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition-colors duration-200' 
              type="submit"
            >
              Simpan
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ModalKategori;
