import React, { useState } from "react";
import { Trash } from "lucide-react"; // Asumsi ini digunakan untuk ikon sampah

function FilterTransaksi({ budgets, akun, tab, transactions, setTransactions, type, onDelete, onEdit }) {
  const [expandedGroups, setExpandedGroups] = useState({});
  const [sortBy, setSortBy] = useState("transaction_date"); // Default ke pengurutan tanggal (terkini)

  const formatGroupKey = (dateStr) => {
    const date = new Date(dateStr);
    // Logika grouping berdasarkan tab (Harian, Bulanan, Tahunan) tetap sama
    if (tab === "Harian") {
      return date.toLocaleDateString("id-ID", {
        weekday: "long", day: "numeric", month: "long", year: "numeric"
      });
    } else if (tab === "Bulanan") {
      return date.toLocaleDateString("id-ID", { month: "long", year: "numeric" });
    } else if (tab === "Tahunan") {
      return date.getFullYear().toString();
    }
    return "Semua Transaksi"; // Fallback jika tab tidak dikenali (ini tidak relevan untuk filter "Jumlah")
  };

  const groupBy = (data) => {
    const grouped = {};
    data.forEach((item) => {
      const key = formatGroupKey(item.transaction_date);
      if (!grouped[key]) grouped[key] = [];
      grouped[key].push(item);
    });
    return grouped;
  };

  const sortItems = (items) => {
    return [...items].sort((a, b) => {
      if (sortBy === "category") {
        return (a.category_name || "").localeCompare(b.category_name || ""); // Menggunakan category_name
      }
      if (sortBy === "akun") {
        return (a.account_name || "").localeCompare(b.account_name || ""); // Menggunakan account_name
      }
      if (sortBy === "name") {
        return (a.description || "").localeCompare(b.description || ""); // Menggunakan description
      }
      if (sortBy === "amount") {
        // Mengurutkan jumlah secara ascending (terkecil ke terbesar)
        return parseFloat(a.amount) - parseFloat(b.amount);
      }
      // Default: Mengurutkan tanggal secara descending (terbaru ke terlama)
      return new Date(b.transaction_date) - new Date(a.transaction_date);
    });
  };

  const grouped = groupBy(transactions);

  const toggleGroup = (groupKey) => {
    setExpandedGroups((prev) => ({
      ...prev,
      [groupKey]: !prev[groupKey],
    }));
  };

  // handleAdd tidak lagi relevan di komponen filter, ini lebih cocok di komponen utama (App.js)
  // karena fungsi ini menambahkan item ke transactions global.

  return (
    <div className="mx-2">
      <div className="mb-4">
        <label className="text-sm font-medium mr-2">Urutkan Berdasarkan : </label>
        <select
          className="text-sm border px-0 py-1 rounded bg-gray-200"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="transaction_date">Tanggal (Terbaru)</option> {/* Opsi default tetap ada */}
          <option value="amount">Jumlah</option> {/* Opsi untuk mengurutkan berdasarkan Jumlah */}
          {type === "Budget" && <option value="category">Kategori</option>}
          {type === "Akun" && <option value="akun">Akun</option>}
          <option value="name">Nama</option>
        </select>
      </div>

      {Object.keys(grouped)
        .sort((a, b) => {
          // Tetap mengurutkan grup berdasarkan tanggal untuk tampilan
          const dateA = new Date(grouped[a][0].transaction_date);
          const dateB = new Date(grouped[b][0].transaction_date);
          return dateB - dateA;
        })
        .map((groupKey) => {
          // groupDate tidak digunakan, bisa dihapus
          const sortedItems = sortItems(grouped[groupKey]); // Item dalam grup diurutkan
          return (
            <div key={groupKey} className="mb-4">
              <div
                className="flex items-center justify-between bg-gray-300 px-4 py-2 rounded cursor-pointer"
                onClick={() => toggleGroup(groupKey)}
              >
                <span className="font-semibold text-gray-800">{groupKey}</span>
                <span>{expandedGroups[groupKey] ? "▲" : "▼"}</span>
              </div>
              {expandedGroups[groupKey] && (
                <>
                  <table className="w-full text-sm text-left text-gray-700 border mt-1">
                    <thead>
                      <tr className="bg-gray-100">
                        <th className="px-2 py-1 border">Nama</th>
                        <th className="px-2 py-1 border">Jumlah</th>
                        {type === "Budget" && <th className="px-2 py-1 border">Kategori</th>}
                        {type === "Akun" && <th className="px-2 py-1 border">Akun</th>}
                        <th className="px-2 py-1 border">Tanggal</th>
                        <th className="px-2 py-1 border"></th>
                      </tr>
                    </thead>
                    <tbody>
                      {sortedItems.map((row) => (
                        <tr key={row.id}>
                          <td className="px-2 py-1 border">{row.description}</td>
                          <td className="px-2 py-1 border">Rp {Number(row.amount).toLocaleString('id-ID')}</td>
                          {/* Pastikan `category_name` ada di objek `row` jika itu transaksi pengeluaran */}
                          {type === "Budget" && <td className="px-2 py-1 border">{row.category_name || "-"}</td>}
                          {/* Pastikan `account_name` ada di objek `row` jika itu transaksi pendapatan */}
                          {type === "Akun" && <td className="px-2 py-1 border">{row.account_name || "-"}</td>}
                          <td className="px-2 py-1 border">
                            {new Date(row.transaction_date).toLocaleDateString("id-ID", { day: 'numeric', month: 'long', year: 'numeric' })}
                          </td>
                          <td className="border px-2 py-1 text-center">
                            <button
                              className="text-sm text-blue-600 hover:underline"
                              onClick={() => onEdit(row)}
                              title="Edit"
                            >
                              Edit
                            </button>
                            <button
                              className="ml-2 text-sm text-red-600 hover:underline"
                              onClick={() => onDelete(row.id)}
                              title="Hapus"
                            >
                              Hapus
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </>
              )}
            </div>
          );
        })}
    </div>
  );
}

export default FilterTransaksi;
