// ModalKategori.js
import React, { useState, useEffect } from 'react';

function ModalKategori({ isOpen, onClose, onSave, initialData, accounts, mode }) {
  const [name, setName] = useState('');
  const [amount, setAmount] = useState(''); // State baru untuk jumlah anggaran

  useEffect(() => {
    if (isOpen) {
      if (initialData && mode === 'edit') {
        setName(initialData.name || '');
        setAmount(''); // Reset amount for edit mode, as it's not directly edited here
      } else { // mode === 'add' or no initialData
        setName('');
        setAmount('');
      }
    }
  }, [isOpen, initialData, mode]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (mode === 'add') {
      if (!name.trim()) {
        alert('Nama kategori tidak boleh kosong!');
        return;
      }
      if (amount.trim() === '' || isNaN(parseFloat(amount))) {
        alert('Jumlah anggaran harus diisi dengan angka yang valid!');
        return;
      }
      // Kirim name dan amount saat menambahkan kategori baru
      onSave({ name: name.trim(), amount: parseFloat(amount) });
    } else if (mode === 'edit') {
      if (!name.trim()) {
        alert('Nama kategori tidak boleh kosong!');
        return;
      }
      // Hanya kirim id dan name saat mengedit kategori
      onSave({ id: initialData.id, name: name.trim() });
    }
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-md mx-4">
        <h2 className="text-2xl font-bold mb-4 text-gray-800">
          {mode === 'add' ? 'Tambah Kategori Baru' : 'Edit Kategori'}
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="name" className="block text-gray-700 text-sm font-bold mb-2">
              Nama Kategori:
            </label>
            <input
              type="text"
              id="name"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          {/* Tampilkan input amount hanya saat mode 'add' */}
          {mode === 'add' && (
            <div className="mb-4">
              <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">
                Jumlah Anggaran (IDR):
              </label>
              <input
                type="number"
                id="amount"
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                required
                min="0" // Pastikan jumlah tidak negatif
              />
            </div>
          )}

          <div className="flex justify-end gap-3">
            <button
              type="button"
              onClick={onClose}
              className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Batal
            </button>
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Simpan
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ModalKategori;
